definition.script:
node() {
    stage('Deploying') {
        build job: 'Initial_Infrastructure_Build', parameters: [string(name: 'appId', value: "${appID}"), password(description: 'Put in the password for the principle account', name: 'passwd', value: "${passwd}"), string(name: 'tenant', value: "${tenant}"), string(name: 'region', value: "${region}"), string(name: 'ResourceName', value: "${ResourceName}")]
    }
    stage('Waiting to Delete Resources'){
        try{
            timeout(time: 120, unit: 'MINUTES') {
                input "continue without waiting..."
            }
        }catch(err) {
           echo "This was timedout automatically." 
        }
    }
    stage('cleanup'){
        build job: 'Delete_Resources', parameters: [string(name: 'ResourceName', value: "${ResourceName}")]
    }

}
