definition.script:
node() {
    stage('Provisioning Azure Resources') {
        build job: 'Initial_Infrastructure_Build', parameters: [string(name: 'appId', value: "${appID}"), password(description: 'Put in the password for the principle account', name: 'passwd', value: "${passwd}"), string(name: 'tenant', value: "${tenant}"), string(name: 'ResourceName', value: "${ResourceName}"), string(name: 'region', value: "${region}"), string(name: 'NumberOfNodes', value: "${NumberOfNodes}"), string(name: 'workingdir', value: "${env.BUILD_TAG}")]

    }
    stage('Configuring the Deployed Resources'){
        try{
            build job: 'Configure_Provisioned_Infrastructure', parameters: [string(name: 'workingdir', value: "${env.BUILD_TAG}")]
        }catch(err){
            echo "I guess the deployment didn't go well."
        }    
    }
    stage('Waiting to Delete Resources'){
        try{
            timeout(time: 120, unit: 'MINUTES') {
                input "continue without waiting..."
            }
        }catch(err) {
           echo "This was timedout automatically." 
        }
    }
    stage('Clean-Up'){
        build job: 'Delete_Resources', parameters: [string(name: 'ResourceName', value: "${ResourceName}"), string(name: 'workingdir', value: "${env.BUILD_TAG}")]
    }

}
